#include "IOMAP.h"

class AFIODriver {
    public:
        enum class PERIPHERAL : uint8_t {
            CAN = 13,
        };
        constexpr AFIODriver(
            PERIPHERAL peripheral, 
            uint8_t remap_value) : 
            peripheral(peripheral), 
            remap_value(remap_value) {}
        AFIODriver() =  delete;
        ~AFIODriver() = default;

        void configure() const;

    private:
        AFIO_TypeDef * const afio() {reinterpret_cast<AFIO_TypeDef *>(AFIO_BASE);};
        PERIPHERAL peripheral;
        uint8_t remap_value;
};
```

This change ensures that your code adheres to the C++ language rules.

